(19,	'Profil Fakultas Ilmu Kesehatan',	'<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:14.0pt\"><span calibri=\"\" style=\"font-family:\">Struktur Program Studi</span></span></b></span></span></p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" height=\"105\" width=\"80%\">\r\n	<tbody>\r\n		<tr>\r\n			<td>1.</td>\r\n			<td>Dekan</td>\r\n			<td>:</td>\r\n			<td>&nbsp;Fenti Dewi Pertiwi, S.Kep., Ners., M.K.M.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2.</td>\r\n			<td>Wakil Dekan Bidang Akademik</td>\r\n			<td>:</td>\r\n			<td>&nbsp;Dr. Asri Masitha Arsyati, S.KM., M.K.M.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>3.</td>\r\n			<td>Wakil Dekan Bidang Pengelolaan Sumber Daya</td>\r\n			<td>:</td>\r\n			<td>&nbsp;Andreanda Nasution, S.KM., M.Kes</td>\r\n		</tr>\r\n		<tr>\r\n			<td>4.</td>\r\n			<td>Wakil Dekan Kemahasiswaan, Kerjasama dan Dakwah</td>\r\n			<td>:</td>\r\n			<td>&nbsp;Andi Asnitatima, S.KM., M.Kes.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\">&nbsp;</p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:14.0pt\"><span calibri=\"\" style=\"font-family:\">Visi</span></span></b></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Menjadi salah satu pusat pendidikan kesehatan yang inovatif dan menghasilkan lulusan Sarjana Kesehatan yang berkualitas dan profesional berlandaskan kesatuan konsep iman, ilmu dan amal</span></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"margin:0cm 0cm 0.0001pt\">&nbsp;</p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:14.0pt\"><span calibri=\"\" style=\"font-family:\">Misi</span></span></b></span></span></p>\r\n\r\n<ol>\r\n	<li class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Menyelenggarakan pendidikan kesehatan untuk menghasilkan sumber daya manusia kesehatan yang kreatif, berahlakul karimah dan relevan dengan dinamika kebutuhan pelayanan kesehatan.</span></span></span></li>\r\n	<li class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Mengembangkan ilmu pengetahuan dan teknologi untuk menghasilkan karya-karya penelitian kesehatan yang inovatif dan berkualitas, sehingga dapat meningkatkan proses pembelajaran. </span></span></span></li>\r\n	<li class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Menyelenggarakan kegiatan pengabdian masyarakat melalui upaya pemberdayaan masyarakat, sehingga masyarakat semakin mampu berpartisipasi aktif dalam proses pembangunan kesehatan yang adil dan berkelanjutan.</span></span></span></li>\r\n	<li class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Membangun kemitraan dengan berbagai satuan pendidikan kesehatan, pemerintah, dunia usaha dan organisasi profesi kesehatan</span></span></span></li>\r\n</ol>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0cm 0cm 8pt\"><span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:14.0pt\">Kontak Fakultas</span></b></span></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Telp. 0251 &ndash; 7553165</span></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Whatsapp : 0825-1835-7804</span></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Email: fikes@uika-bogor.ac.id</span></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Http://fikes.uika-bogor.ac.id</span></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/cHTBMn_dfHQ\" title=\"YouTube video player\" width=\"560\"></iframe></p>\r\n',	'',	'',	'profil-fakultas-ilmu-kesehatan',	10776,	'UIKA',	'f1cc3ad43f48c650e12b03cd62abdaa7',	'ID'),
(20,	'Profil Sekolah Pascasarjana',	'<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:14.0pt\"><span calibri=\"\" style=\"font-family:\">Struktur Program Studi</span></span></b></span></span></p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" height=\"105\" width=\"80%\">\r\n	<tbody>\r\n		<tr>\r\n			<td>1.</td>\r\n			<td>Direktur</td>\r\n			<td>:</td>\r\n			<td>&nbsp;Prof. Dr. K.H. Didin Hafidhuddin, M.S</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2.</td>\r\n			<td>Wakil Direktur Bid. Akademik, Inovasi dan Kemahasiswaan</td>\r\n			<td>:</td>\r\n			<td>&nbsp;H. Hendri Tanjung, M.M., Ph.D</td>\r\n		</tr>\r\n		<tr>\r\n			<td>3.</td>\r\n			<td>Wakil Direktur Bid. Sumberdaya, Kerjasama dan Dakwah</td>\r\n			<td>:</td>\r\n			<td>Dr. H. Ibdalsyah, M.A.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\">&nbsp;</p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span style=\"font-size:14.0pt\"><span calibri=\"\" style=\"font-family:\">Visi</span></span></b></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\">&ldquo;Menjadi pusat kajian keilmuan unggulan dengan pendekatan integral dan Islami&rdquo;</p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\">&nbsp;</p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span lang=\"EN-ID\" style=\"font-size:14.0pt\"><span calibri=\"\" style=\"font-family:\">Misi</span></span></b></span></span></p>\r\n\r\n<p>Menyelenggarakan kegiatan pendidikan, penelitian serta pengkajian secara terpadu dan aplikatif berlandaskan ajaran Islam</p>\r\n\r\n<p><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><b><span lang=\"EN-ID\" style=\"font-size:14.0pt\"><span calibri=\"\" style=\"font-family:\">Tujuan</span></span></b></span></span></p>\r\n\r\n<ol>\r\n	<li>Menghasilkan lulusan yang mempunyai kemampuan mengembangkan konsep baru di bidang ilmu atau profesinya</li>\r\n	<li>Menghasilkan lulusan yang mempunyai kemampuan melaksanakan, mengorganisasikan dan memimpin program penelitian.</li>\r\n	<li>Menghasilkan lulusan yang mempunyai kemampuan melakukan pendekatan interdisipliner bagi penerapan keahliannya secara profesional.</li>\r\n	<li>Menghasilkan lulusan yang mampu memberikan solusi atas problem yang ada di tengah masyarakat sesuai dengan disiplin ilmu yang dikuasainya.</li>\r\n</ol>\r\n\r\n<p><em><strong>Sasaran</strong></em></p>\r\n\r\n<ol>\r\n	<li>Menghasilkan lulusan yang mempunyai kemampuan dalam bidang manajemen, pendidikan Islam dan ekonomi Islam melalui kajian dan penelitian ilmiah; dengan ditemukannya konsep-konsep di dalam manajemen, pendidikan Islam dan ekonomi Islam melalui kajian dan penelitian ilmiah</li>\r\n	<li>Menghasilkan lulusan yang mempunyai kompetensi dan keterampilan dalam mengorganisir kegiatan sesuai dengan bidang pekerjaanya.</li>\r\n	<li>Menghasilkan lulusan yang mampu memberikan sumbangan pemikiran bagi kemajuan masyarakat, bangsa dan agama dalam bidang manajemen, pendidikan Islam dan ekonomi Islam.</li>\r\n</ol>\r\n\r\n<p style=\"margin-bottom:.0001pt; margin:0cm 0cm 8pt\"><span style=\"font-size:11pt\"><span style=\"line-height:normal\"><span style=\"font-family:Calibri,sans-serif\"><b><span style=\"font-size:14.0pt\">Kontak Fakultas</span></b></span></span></span></p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size: 16px;\">Telpn</span>: 0251-7559354</p>\r\n\r\n<p class=\"MsoNoSpacing\" style=\"text-align:justify; margin:0cm 0cm 0.0001pt\"><span style=\"font-size:12pt\"><span new=\"\" roman=\"\" style=\"font-family:\" times=\"\"><span calibri=\"\" style=\"font-family:\">Email: </span></span></span>info@ppsuika.ac.id</p>\r\n\r\n<div style=\"text-align: justify;\"><a href=\"http://ppsuika.ac.id\" target=\"_blank\"><span style=\"font-size: 16px;\">http://ppsuika.ac.id</span></a></div>\r\n\r\n<p style=\"text-align: center;\"><iframe allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen=\"\" frameborder=\"0\" height=\"315\" src=\"https://www.youtube.com/embed/TVmsa7q1rVU\" title=\"YouTube video player\" width=\"560\"></iframe></p>\r\n',	'',	'',	'profil-sekolah-pascasarjana',	11023,	'UIKA',	'5279ac1e1f44eba67c5da1057f406132',	'ID'),
